import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

function PdfManager() {
  const { id } = useParams();
  const history = useHistory();
  const [pdf, setPdf] = useState(null);
  const [stampData, setStampData] = useState({ date: '', name: '', comment: '' });

  useEffect(() => {
    axios.get(`http://localhost:8080/api/pdfs/${id}`)
      .then(response => setPdf(response.data))
      .catch(error => console.log(error));
  }, [id]);

  const handleStampChange = (e) => {
    const { name, value } = e.target;
    setStampData({ ...stampData, [name]: value });
  };

  const handleStamp = () => {
    axios.post(`http://localhost:8080/api/pdfs/${id}/stamp`, stampData)
      .then(response => {
        console.log('Stamp applied');
        setPdf(response.data);  // Update PDF data to reflect the stamp
      })
      .catch(error => console.log(error));
  };

  const handleDelete = () => {
    axios.delete(`http://localhost:8080/api/pdfs/${id}`)
      .then(() => {
        console.log('PDF deleted');
        history.push('/');  // Redirect to the list after deletion
      })
      .catch(error => console.log(error));
  };

  const handleDownload = () => {
    axios.get(`http://localhost:8080/api/pdfs/${id}/download`, { responseType: 'blob' })
      .then(response => {
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', `${pdf.name}`);
        document.body.appendChild(link);
        link.click();
      })
      .catch(error => console.log(error));
  };

  return (
    <div>
      <h2>Manage PDF</h2>
      {pdf && (
        <div>
          <img src={`http://localhost:8080/api/pdfs/${id}/preview`} alt="Preview" />
          <div>
            <h3>Stamp Information</h3>
            <input
              type="text"
              name="date"
              placeholder="Date"
              value={stampData.date}
              onChange={handleStampChange}
            />
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={stampData.name}
              onChange={handleStampChange}
            />
            <input
              type="text"
              name="comment"
              placeholder="Comment"
              value={stampData.comment}
              onChange={handleStampChange}
            />
            <button onClick={handleStamp}>Apply Stamp</button>
          </div>
          <button onClick={handleDownload}>Download PDF</button>
          <button onClick={handleDelete}>Delete PDF</button>
        </div>
      )}
    </div>
  );
}

export default PdfManager;
