import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function PdfList() {
  const [pdfs, setPdfs] = useState([]);

  // Fetch PDFs on mount
  useEffect(() => {
    fetchPdfs();
  }, []);

  const fetchPdfs = () => {
    axios.get('http://localhost:8080/api/pdfs')
      .then(response => {
        setPdfs(response.data);
      })
      .catch(error => {
        console.error('Error fetching PDFs:', error);
      });
  };

  // Upload a new PDF
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    axios.post('http://localhost:8080/api/pdfs/upload', formData)
      .then(() => {
        console.log('File uploaded successfully.');
        // Re-fetch the PDFs to see the newly uploaded file
        fetchPdfs();
      })
      .catch(err => {
        console.error('Error uploading file:', err);
      });
  };

  const openFileDialog = () => {
    document.getElementById('pdfFileInput').click();
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h1>Uploaded PDFs</h1>

      <button onClick={openFileDialog}>Add New File</button>
      <input
        id="pdfFileInput"
        type="file"
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />

      <ul style={{ marginTop: '1rem' }}>
        {pdfs.length === 0 ? (
          <li>No PDFs uploaded yet.</li>
        ) : (
          pdfs.map((pdf) => (
            <li key={pdf.id} style={{ marginBottom: '0.5rem' }}>
              {/* 
                1) Display the PDF name (adjust the property if your backend returns "filename" instead of "name")
                2) Make it clickable via a <Link> to /pdf/:id
              */}
              <Link to={`/pdf/${pdf.id}`}>
                {pdf.name || pdf.fileName}
              </Link>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default PdfList;
