import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';

const PdfList = () => {
  const [pdfs, setPdfs] = useState([]);
  const fileInputRef = useRef(null);

  // Fetch existing PDFs when component mounts
  useEffect(() => {
    fetchPdfs();
  }, []);

  // Helper function to get PDFs from the server
  const fetchPdfs = () => {
    axios.get('http://localhost:8080/api/pdfs')
      .then(response => {
        setPdfs(response.data);
      })
      .catch(error => {
        console.error('Error fetching PDFs:', error);
      });
  };

  // Open the hidden file input
  const openFileDialog = () => {
    fileInputRef.current.click();
  };

  // Handle file selection and upload to backend
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    console.log("File selected:", file);

    const formData = new FormData();
    formData.append("file", file);

    axios.post('http://localhost:8080/api/pdfs/upload', formData)
      .then(response => {
        console.log("File uploaded successfully:", response.data);
        // Re-fetch the PDFs to update the list
        fetchPdfs();

        // If you prefer, you can manually add the new PDF to state here instead of re-fetching:
        // setPdfs([...pdfs, response.data]);
      })
      .catch(error => {
        console.error("Error uploading file:", error);
      });
  };

  return (
    <div>
      <h1>Uploaded PDFs</h1>
      <button onClick={openFileDialog}>Add New File</button>

      {/* Hidden file input that is programmatically clicked */}
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        onChange={handleFileChange}
      />

      <ul>
        {pdfs.length === 0 ? (
          <li>No PDFs uploaded yet.</li>
        ) : (
          pdfs.map((pdf) => (
           <li key={pdf.id}>
             <a href={pdf.url} target="_blank" rel="noopener noreferrer">
               {pdf.name}
             </a>
           </li>

          ))
        )}
      </ul>
    </div>
  );
};

export default PdfList;
