import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { Dialog, DialogTitle, DialogContent, DialogActions, Button } from "@mui/material";

function PdfManager() {
  const navigate = useNavigate();
  const [pdfs, setPdfs] = useState([]);
  const [uploadDialogOpen, setUploadDialogOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadError, setUploadError] = useState(null);

  // Navigate back to the home page
  const goBack = () => {
    navigate("/");
  };

  // Fetch existing PDF data from the server on component mount
  useEffect(() => {
    fetch("/api/pdfs")
      .then((res) => res.json())
      .then((data) => {
        setPdfs(data);
      })
      .catch((err) => console.error("Error fetching PDFs:", err));
  }, []);

  // Open the upload dialog and reset any previous state
  const handleOpenUploadDialog = () => {
    setSelectedFile(null);
    setUploadError(null);
    setUploadDialogOpen(true);
  };

  // Close the upload dialog
  const handleCloseUploadDialog = () => {
    setUploadDialogOpen(false);
  };

  // Handle file selection within the dialog
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file && file.type === "application/pdf") {
      setSelectedFile(file);
      setUploadError(null);
    } else {
      setSelectedFile(null);
      setUploadError("Please select a valid PDF file.");
    }
  };

  // Handle the file upload process
  const handleUpload = () => {
    if (!selectedFile) {
      setUploadError("No file selected.");
      return;
    }
    setUploading(true);
    setUploadError(null);

    const formData = new FormData();
    formData.append("pdf", selectedFile);

    // Adjust the endpoint based on your backend configuration
    fetch("/api/pdfs/upload", {
      method: "POST",
      body: formData,
    })
      .then((res) => res.json())
      .then((uploaded) => {
        // Assuming your server returns an object with a fileId
        setPdfs((prev) => [
          ...prev,
          {
            id: uploaded.fileId,
            name: selectedFile.name,
            url: `/uploads/${uploaded.fileId}`, // Adjust URL if needed
          },
        ]);
        setUploading(false);
        setUploadDialogOpen(false);
      })
      .catch((err) => {
        console.error("Error uploading file:", err);
        setUploadError("Error uploading file.");
        setUploading(false);
      });
  };

  return (
    <div>
      <h1>PDF Manager</h1>
      <button onClick={goBack}>Go Back</button>
      <button onClick={handleOpenUploadDialog}>Add New File</button>

      {/* Upload Dialog */}
      <Dialog open={uploadDialogOpen} onClose={handleCloseUploadDialog}>
        <DialogTitle>Upload PDF</DialogTitle>
        <DialogContent>
          <input type="file" accept="application/pdf" onChange={handleFileChange} />
          {uploadError && <p style={{ color: "red" }}>{uploadError}</p>}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseUploadDialog} disabled={uploading}>
            Cancel
          </Button>
          <Button onClick={handleUpload} disabled={uploading || !selectedFile}>
            {uploading ? "Uploading..." : "Upload"}
          </Button>
        </DialogActions>
      </Dialog>

      <hr />

      <h2>Uploaded PDFs</h2>
      {pdfs.length === 0 ? (
        <p>No PDFs uploaded yet.</p>
      ) : (
        <ul>
          {pdfs.map((pdf) => (
            <li key={pdf.id}>
              <a href={pdf.url} target="_blank" rel="noopener noreferrer">
                {pdf.name}
              </a>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default PdfManager;
