import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function PdfManager() {
  const { id } = useParams(); // PDF ID from the route
  const navigate = useNavigate();

  const [pdf, setPdf] = useState(null);
  const [stampDate, setStampDate] = useState('');
  const [stampName, setStampName] = useState('');
  const [stampComment, setStampComment] = useState('');

  useEffect(() => {
    // Optionally fetch PDF details if needed
    axios.get(`http://localhost:8080/api/pdfs/${id}`)
      .then(response => {
        setPdf(response.data); // e.g., { id: 1, name: 'Doc.pdf', ... }
      })
      .catch(error => {
        console.error('Error fetching PDF details:', error);
      });
  }, [id]);

  const goBack = () => {
    navigate('/');
  };

  // Handle stamping
  const handleStampSubmit = () => {
    axios.post(`http://localhost:8080/api/pdfs/${id}/stamp`, {
      date: stampDate,
      name: stampName,
      comment: stampComment
    })
    .then(response => {
      console.log('Stamp applied:', response.data);
      // Reset fields, navigate back, or show success message
      setStampDate('');
      setStampName('');
      setStampComment('');
      navigate('/'); // Go back to the list
    })
    .catch(error => {
      console.error('Error stamping PDF:', error);
    });
  };

  return (
    <div style={{ padding: '1rem' }}>
      <h1>Stamp PDF</h1>
      <button onClick={goBack}>Go Back</button>

      {pdf && (
        <div style={{ marginTop: '1rem' }}>
          <h2>{pdf.name || pdf.filename}</h2>
          <div style={{ marginBottom: '1rem' }}>
            <label>
              Date:&nbsp;
              <input
                type="date"
                value={stampDate}
                onChange={(e) => setStampDate(e.target.value)}
              />
            </label>
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label>
              Name:&nbsp;
              <input
                type="text"
                value={stampName}
                onChange={(e) => setStampName(e.target.value)}
                placeholder="John Doe"
              />
            </label>
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <label>
              Comment:&nbsp;
              <input
                type="text"
                value={stampComment}
                onChange={(e) => setStampComment(e.target.value)}
                placeholder="Approved"
              />
            </label>
          </div>
          <button onClick={handleStampSubmit}>Apply Stamp</button>
        </div>
      )}
    </div>
  );
}

export default PdfManager;
