import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const PdfList = () => {
  const [pdfs, setPdfs] = useState([]);
  const fileInputRef = useRef(null);

  useEffect(() => {
    fetchPdfs();
  }, []);

  const fetchPdfs = () => {
    axios.get('http://localhost:8080/api/pdfs')
      .then(response => {
        console.log('PDF data from backend:', response.data);
        setPdfs(response.data);
      })
      .catch(error => {
        console.error('Error fetching PDFs:', error);
      });
  };

  const openFileDialog = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    console.log("File selected:", file);

    const formData = new FormData();
    formData.append("file", file);

    axios.post('http://localhost:8080/api/pdfs/upload', formData)
      .then(response => {
        console.log("File uploaded successfully:", response.data);
        // Re-fetch the PDFs
        fetchPdfs();
      })
      .catch(error => {
        console.error("Error uploading file:", error);
      });
  };

  return (
    <div>
      <h1>Uploaded PDFs</h1>
      <button onClick={openFileDialog}>Add New File</button>
      <input
        type="file"
        ref={fileInputRef}
        style={{ display: 'none' }}
        onChange={handleFileChange}
        accept=".pdf"
      />

     <ul style={{ listStyle: 'none', padding: '10px' }}>
       {pdfs.length === 0 ? (
         <li>No PDFs uploaded yet.</li>
       ) : (
         pdfs.map((pdf) => ( 
          <li key={pdf.id} style={{ margin: '10px 0', padding: '10px', border: '1px solid #ddd', borderRadius: '4px' }}>
            <span>{pdf.fileName}</span>
            <Link 
              to={`/pdfs/${pdf.id}`} 
              style={{ 
                marginLeft: '15px', 
                padding: '5px 10px', 
                backgroundColor: '#4CAF50', 
                color: 'white', 
                textDecoration: 'none', 
                borderRadius: '4px' 
              }}
            >
              Manage PDF
            </Link>
          </li>
         ))
       )}
     </ul>
    </div>
  );
};

export default PdfList;
